mapper.insert:
insert 
	member 
set 
	memberId = #{memberId}
	, memberPw = #{memberPw}
	, memberFirstName = #{memberFirstName}
	, memberLastName = #{memberLastName}
	, memberNickName = #{memberNickName}
	, memberGenderCd = #{memberGenderCd}
	, memberDob = #{memberDob}
	, memberEmail = #{memberEmail}

mapper.update:
update 
	member 
set 
	memberNickName = #{memberNickName}
	, memberPw = #{memberPw}
where 
	memberId = #{memberId}
